import React, { Component } from "react";
import "./Compiler.css";
import io from "socket.io-client";


const ENDPOINT="http://localhost:5000";


let socket=io(ENDPOINT);

export default class Compiler extends Component {
  constructor(props) {
    super(props);
    this.state = {
      input: localStorage.getItem("input") || ``,
      output: ``,
      language_id: localStorage.getItem("language_Id") || 2,
      user_input: ``
    };
  }
  input = (event) => {
    event.preventDefault();
    this.setState({ input: event.target.value });
    localStorage.setItem("input", event.target.value);
     // If language changes on one socket, emit to all other
    socket.emit('code-change', localStorage.getItem("input"));
    console.log("CODE-CHANGE: " + localStorage.getItem("input"));

  };

  userInput = (event) => {
    event.preventDefault();
    this.setState({ user_input: event.target.value });
  };
  language = (event) => {
    event.preventDefault();

    this.setState({ language_id: event.target.value });
    localStorage.setItem("language_Id", event.target.value);
  };


  submit = async (e) => {
    e.preventDefault();

    let outputText = document.getElementById("output");
    outputText.innerHTML = "";
    outputText.innerHTML += "Creating Submission ...\n";
    const response = await fetch(
      "https://judge0-ce.p.rapidapi.com/submissions",
      {
        method: "POST",
        headers: {
          "x-rapidapi-host": "judge0-ce.p.rapidapi.com",
          "x-rapidapi-key":
            "694d47ada2msh1d7f6faae8cc7eap1147bajsn1e3c5d45ab87", // Get yours for free at https://rapidapi.com/judge0-official/api/judge0-ce/
          "content-type": "application/json",
          accept: "application/json"
        },
        body: JSON.stringify({
          source_code: this.state.input,
          stdin: this.state.user_input,
          language_id: this.state.language_id
        })
      }
    );
    outputText.innerHTML += "Submission Created ...\n";
    const jsonResponse = await response.json();

    let jsonGetSolution = {
      status: { description: "Queue" },
      stderr: null,
      compile_output: null
    };

    while (
      jsonGetSolution.status.description !== "Accepted" &&
      jsonGetSolution.stderr == null &&
      jsonGetSolution.compile_output == null
    ) {
      outputText.innerHTML = `Creating Submission ... \nSubmission Created ...\nChecking Submission Status\nstatus : ${jsonGetSolution.status.description}`;
      if (jsonResponse.token) {
        let url = `https://judge0-ce.p.rapidapi.com/submissions/${jsonResponse.token}?base64_encoded=true`;

        const getSolution = await fetch(url, {
          method: "GET",
          headers: {
            "x-rapidapi-host": "judge0-ce.p.rapidapi.com",
            "x-rapidapi-key":
              "694d47ada2msh1d7f6faae8cc7eap1147bajsn1e3c5d45ab87", // Get yours for free at https://rapidapi.com/judge0-official/api/judge0-ce/
            "content-type": "application/json"
          }
        });

        jsonGetSolution = await getSolution.json();
      }
    }
    if (jsonGetSolution.stdout) {
      const output = atob(jsonGetSolution.stdout);

      outputText.innerHTML = "";

      outputText.innerHTML += `Results :\n${output}\nExecution Time : ${jsonGetSolution.time} Secs\nMemory used : ${jsonGetSolution.memory} bytes`;
    } else {
      outputText.innerHTML = "Your code has some error";
    }
  };
  render() {
    return (
      <>
        <div className="row container-fluid">
          <div className="col-8">
            <label htmlFor="solution">
              <span className="badge badge-info heading mt-2">
                <i className="fas fa-code fa-fw fa-lg"></i> Code Here
              </span>
            </label>
            <textarea
              required
              name="solution"
              id="source"
              onChange={this.input}
              className=" source"
              value={this.state.input}></textarea>

            <button
              type="submit"
              className="btn btn-danger"
              onClick={this.submit}>
              <i className="fas fa-cog fa-fw"></i> Run
            </button>

            <label htmlFor="tags" className="">
              <b className="heading">Language:</b>
            </label>
            <select
              value={this.state.language_id}
              onChange={this.language}
              id="tags"
              className="form-control form-inline mb-2 language">
              <option value="54">C++</option>
              <option value="50">C</option>
              <option value="62">Java</option>
              <option value="71">Python</option>
            </select>
          </div>
        <div className="row mt-2 ml-4">
          <div className="col-3">
            <span className="badge badge-primary heading my-2">
              <i className="fas fa-user fa-fw fa-md"></i> User Input
            </span>
            <br />
            <textarea id="input" onChange={this.userInput}></textarea>
          </div>
          <div className="col-3">
            <span className="badge badge-info heading my-2">
              <i className="fas fa-exclamation fa-fw fa-md"></i> Output
            </span>
            <br />
            <textarea id="output"></textarea>
          </div>
          </div>
          </div>
      </>
    );
  }
}
